import React, {Component} from "react"
import Navigation from './components/Navigation/Navigation'
import Logo from './components/Logo/Logo'
import Rank from './components/Rank/Rank'
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm'
import './App.css';
import Clarifai from 'clarifai'


const app = new Clarifai.App({
  apiKey: 'ad94ff7f5e664317b6b76f0fcfff1da8'
})
class App extends Component {
  constructor() {
    super()
    this.state = {
      /*We need to set both the input and the imageUrl as states, for the following reasons:
          1. The input needs to be a state because when we want to detect a different image, we would want to detect from the new image, not the old one
          2. The imageUrl needs to be a 



    */
      input: '',
    }
  }
  onInputChange = (event) => {
    this.setState({input: event.target.value})
  }

  onButtonSubmit = () => {
    console.log("click")
    app.models.predict(
      Clarifai.COLOR_MODEL, 
      this.state.input)
    .then(
      function(response){
        console.log(response)
      },
      function(err){
        console.log(err)
      })
  }

  render() {
    return (
      <div className="App">
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
        onInputChange = {this.onInputChange} 
        onButtonSubmit = {this.onButtonSubmit}/>
        <FaceRecognition input={this.state.input}/>
      </div>
    )
  }
}



export default App;
